    screenReaderLabel="Loading completion"
    meterColor="danger"
    valueNow={40}
    valueMax={60}
    margin="0 0 small"
    renderValue={({ valueNow, valueMax }) => {
    return (
      <span>
        {Math.round(valueNow / valueMax * 100)}%
