messages.description:
Displays messages and validation for the input. It should be an array of
objects with the following shape:
`{
  text: ReactNode,
  type: One of: ['error', 'hint', 'success', 'screenreader-only']
}`

tsType.raw:
(
  event: React.SyntheticEvent,
  data: {
    value?: string | number
    id?: string
  }
) => void
